<script>
import TitlePage from '@/components/Title.vue';
// import mapgl from '2gis-maps';
// import { yandexMap, ymapMarker } from 'vue-yandex-maps';

import axios from 'axios';
export default {
	name: 'ContactsPage',
	components: {
		TitlePage,
		// yandexMap,
		// ymapMarker,
	},
	props: {
		curtains: String,
	},
	data() {
		return {
			inputs: {
				name: '',
				email: '',
				about: '',
				phone: '',
				checked: false,
			},
			disabled: false,
			address: 0,
		};
	},
	methods: {
		onSubmit() {
			const button = document.querySelector('.contacts-form__submit');
			const params = new URLSearchParams();
			params.append('name', this.inputs.name);
			params.append('email', this.inputs.email);
			params.append('about', this.inputs.about);
			params.append('phone', this.inputs.phone);
			let data = this;
			axios
				.post('/send.php', params, {
					headers: {
						'content-type': 'application/x-www-form-urlencoded',
					},
				})
				.then(function (e) {
					button.innerText = 'Отправляется...';
					button.classList.add('is-sending');
					setTimeout(() => {
						button.innerText = 'Отправлено';
						button.classList.remove('is-sending');
						data.disabled = true;
						data.inputs = {
							name: '',
							email: '',
							about: '',
							phone: '',
							checked: false,
						};
					}, 2000);
				})
				.catch(function () {
					data.inputs = {
						name: '',
						email: '',
						about: '',
						phone: '',
						checked: false,
					};
					data.disabled = true;
					button.classList.add('is-error');
					button.innerText = 'Попробуйте позже';
				});
			// let request = new XMLHttpRequest();
			// request.open('POST', url, true);
			// request.setRequestHeader('Content-Type', 'application/json');
			// request.onreadystatechange = function () {
			// 	if (request.readyState === 4 && request.status === 200) {
			// 		var jsonData = JSON.parse(request.response);
			// 		console.log(jsonData);
			// 	}
			// };
			// let data = JSON.stringify({ name: this.$data.inputs.name, email: this.$data.inputs.email, about: this.$data.inputs.about, phone: this.$data.inputs.phone });
			// request.send(data);
		},
		goTo(address) {
			this.address = address;
			// this.map.panTo(this.offices[address]);
		},
	},
};
</script>

